
public class Arrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {1,2,3,4,5};
		for(int i=0;i<=4;i++) {
			if(a[i]%2==0) {
				System.out.println("number is even:" + a[i]);
			}
			else {
				System.out.println("number is odd:" + a[i]);
			}
		}

	}

}
